package Dec2020Leetcode;

import java.util.HashMap;

public class _0387FirstUniqueCharacterInAString {

	public static void main(String[] args) {
		System.out.println(firstUniqChar("leetcode"));
		System.out.println(firstUniqChar("loveleetcode"));
	}

	public static int firstUniqChar(String s) {
		HashMap<Character, Integer> map = new HashMap<Character, Integer>();
		for (int i = 0; i < s.length(); i++) {
			char c = s.charAt(i);
			map.compute(c, (k, v) -> v == null ? 1 : v + 1);
		}

		for (int i = 0; i < s.length(); i++) {
			char c = s.charAt(i);
			if (map.get(c) == 1)
				return i;
		}
		return -1;
	}

}

package MinimumWindowSubsequence;


class Solution {

    public String minWindow(String S, String T) {
        int n, l;
        n = S.length();
        l = T.length();
        int[][] dp = new int[n][l];

        int s, e, len = Integer.MAX_VALUE;
        s = e = 0;

        for (int i = 0; i < n; i++) {
            for (int j = 0; j < l; j++) {
                if (S.charAt(i) == T.charAt(j)) {
                    dp[i][j] = 1 + (i > 0 && j > 0 ? dp[i - 1][j - 1] : 0);
                } else {
                    dp[i][j] = Math.max(
                            i > 0 ? dp[i - 1][j] : 0,
                            j > 0 ? dp[i][j - 1] : 0
                    );
                }
            }
            if (dp[i][l - 1] == l) {

                int j = i, k = l - 1;
                while (k >= 0) {
                    if (S.charAt(j) == T.charAt(k)) {
                        k--;
                    }
                    j--;
                }
                if (len > i - j) {
                    len = i - j;
                    s = j + 1;
                    e = i;
                }
            }
        }
        if (len < Integer.MAX_VALUE) {
            return S.substring(s, e + 1);
        }
        return "";
    }

}

package Graph;

import java.util.Arrays;




public class Graph {
    final private int size;
    final private int[][] matrix;
    Graph(int size)
    {
        this.size=size;
        matrix=new int[size][size];
    }
    Graph()
    {
        size=10;
        matrix=new int[size][size];
    }

    public void createLink(int a,int b)
    {
        try
        {
            matrix[a][b]=1;
            matrix[b][a]=1;
        }
        catch(Exception e)
        {
            System.out.println("ERROR: Link creation failed: "+a+","+b);
        }
    }
    @Override
    public String toString()
    {
        String ret="";
        int v=0;
        for(int[]i:matrix)
        {
            ret+=v+++": "+Arrays.toString(i)+"\n";
        }
        return ret;
    }

    public boolean isLinked(int a,int b)
    {
        System.out.println("a="+a+" b="+b);
        if(a>size || b>size)
            return false;
        if(matrix[a][b]!=0 ||matrix[b][a]!=0)
            return true;
        for(int i=a+1;i<size;i++)
        {

            if(matrix[a][i]!=0)
            {
                if(isLinked(i,b))
                    return true;
            }
        }
        return false;
    }
    public int getShortestPath(int a,int b)
    {
        int len=0;
        int r=getLen(a,b,len);
        return (r==Integer.MAX_VALUE)?-1:r;
    }
    private int getLen(int a,int b,int len)
    {

        if(matrix[a][b]!=0)
            return len+matrix[a][b];
        int ret=Integer.MAX_VALUE;
        for(int i=a+1;i<size;i++)
        {
            if(matrix[a][i]!=0)
            {
               int tmp=getLen(i,b,len+matrix[a][i]);
               if(tmp!=Integer.MAX_VALUE)
               {
                   if(tmp<ret)
                       ret=tmp;
               }
            }
        }
        return ret;
    }
}

