package com.github.cschen1205.jp.chap02;

import java.util.HashMap;
import java.util.HashSet;
import java.util.Set;


public class Q01 {
    public static class Node {
        public Node next;
        public String data;
    }


    public static void deleteDuplicates(Node head) {
        Set<String> unique = new HashSet<>();
        Node x = head;
        Node prev = null;
        while(x != null) {
            if(unique.contains(x.data)){
                prev.next = x.next;
            } else {
                unique.add(x.data);
                prev = x;
            }
            x = x.next;
        }
    }


    public static void deleteDuplicates2(Node head) {
        Node x = head;
        Node prev = null;
        while(x != null) {
            boolean duplicated = false;
            Node runner = head;
            while(runner != x) {
                if(runner.data.equals(x.data)) {
                    duplicated = true;
                    break;
                }
                runner = runner.next;
            }
            if(duplicated) {
                prev.next = x.next;
            } else {
                prev = x;
            }
            x = x.next;
        }
    }
}

package org.assertj.core.util;

import java.util.Comparator;

public class CaseInsensitiveStringComparator implements Comparator<String> {

  public final static CaseInsensitiveStringComparator instance = new CaseInsensitiveStringComparator();

  @Override
  public int compare(String s1, String s2) {
    if (s1 == null && s2 == null) return 0;
    if (s1 == null) return -1;
    if (s2 == null) return 1;
    return s1.toLowerCase().compareTo(s2.toLowerCase());
  }
}

