package com.github.trytocatch.regexreplacer.model.expression.utils;


public class MathOperationUtil {

	public static boolean hasDecimal(Object... args) {
		if (args == null)
			return false;
		for (Object num : args) {
			if (num == null)
				continue;
			if (num instanceof Number) {
				if (num instanceof Double || num instanceof Float)
					return true;
			} else {
				if (num.toString().indexOf('.') >= 0)
					return true;
			}
		}
		return false;
	}


	public static Number[] convertToSameType(boolean convertNullToZero,
			Object... args) {
		Number result[];
		int n;
		if (args == null)
			return new Long[0];
		n = 0;
		if (hasDecimal(args)) {
			result = new Double[args.length];
			for (Object o : args) {
				if (o == null)
					result[n++] = convertNullToZero ? 0D : null;
				else if (o instanceof Number)
					if (o instanceof Double)
						result[n++] = (Double) o;
					else
						result[n++] = new Double(((Number) o).doubleValue());
				else if (o.toString().isEmpty())
					result[n++] = 0D;
				else
					result[n++] = Double.parseDouble(o.toString());
			}
		} else {
			result = new Long[args.length];
			for (Object o : args) {
				if (o == null)
					result[n++] = convertNullToZero ? 0L : null;
				else if (o instanceof Number)
					if (o instanceof Long)
						result[n++] = (Long) o;
					else
						result[n++] = new Long(((Number) o).longValue());
				else if (o.toString().isEmpty())
					result[n++] = 0L;
				else
					result[n++] = Long.parseLong(o.toString());
			}
		}
		return result;
	}
}

package fr.anatom3000.gwwhit.util;

import java.util.HashMap;
import java.util.Map;
import java.util.function.BiFunction;

public class CachingTransformer<TVal, TParam> implements BiFunction<TVal, TParam, TVal> {
    private final Map<TVal, TVal> cache = new HashMap<>();
    private final BiFunction<TVal, TParam, TVal> transformer;

    public CachingTransformer(BiFunction<TVal, TParam, TVal> transformer) {
        this.transformer = transformer;
    }

    @Override
    public TVal apply(TVal val, TParam param) {
        if (!cache.containsKey(val))
            cache.put(val, transformer.apply(val, param));
        return cache.get(val);
    }
}

