package org.bson.json;

import java.lang.reflect.InvocationTargetException;
import java.lang.reflect.Method;
import java.time.Instant;
import java.time.ZoneId;
import java.time.ZonedDateTime;
import java.time.format.DateTimeParseException;
import java.time.temporal.TemporalAccessor;
import java.time.temporal.TemporalQuery;
import java.util.Calendar;
import java.util.TimeZone;

import static java.time.format.DateTimeFormatter.ISO_OFFSET_DATE_TIME;

final class DateTimeFormatter {
    private static final FormatterImpl FORMATTER_IMPL;

    static {
        FormatterImpl dateTimeHelper;
        try {
            dateTimeHelper = loadDateTimeFormatter("org.bson.json.DateTimeFormatter$Java8DateTimeFormatter");
        } catch (LinkageError e) {

            dateTimeHelper = loadDateTimeFormatter("org.bson.json.DateTimeFormatter$JaxbDateTimeFormatter");
        }

        FORMATTER_IMPL = dateTimeHelper;
    }

    private static FormatterImpl loadDateTimeFormatter(final String className) {
        try {
            return (FormatterImpl) Class.forName(className).getDeclaredConstructor().newInstance();
        } catch (ClassNotFoundException e) {

            throw new ExceptionInInitializerError(e);
        } catch (InstantiationException e) {

            throw new ExceptionInInitializerError(e);
        } catch (IllegalAccessException e) {

            throw new ExceptionInInitializerError(e);
        } catch (NoSuchMethodException e) {
            throw new ExceptionInInitializerError(e);
        } catch (InvocationTargetException e) {
            throw new ExceptionInInitializerError(e);
        }
    }

    static long parse(final String dateTimeString) {
        return FORMATTER_IMPL.parse(dateTimeString);
    }

    static String format(final long dateTime) {
        return FORMATTER_IMPL.format(dateTime);
    }

    private interface FormatterImpl {
        long parse(String dateTimeString);
        String format(long dateTime);
    }


    static class JaxbDateTimeFormatter implements FormatterImpl {

        private static final Method DATATYPE_CONVERTER_PARSE_DATE_TIME_METHOD;
        private static final Method DATATYPE_CONVERTER_PRINT_DATE_TIME_METHOD;

        static {
            try {
                DATATYPE_CONVERTER_PARSE_DATE_TIME_METHOD = Class.forName("javax.xml.bind.DatatypeConverter")
                                                                    .getDeclaredMethod("parseDateTime", String.class);
                DATATYPE_CONVERTER_PRINT_DATE_TIME_METHOD = Class.forName("javax.xml.bind.DatatypeConverter")
                                                                    .getDeclaredMethod("printDateTime", Calendar.class);
            } catch (NoSuchMethodException e) {
                throw new ExceptionInInitializerError(e);
            } catch (ClassNotFoundException e) {
                throw new ExceptionInInitializerError(e);
            }
        }

        @Override
        public long parse(final String dateTimeString) {
            try {
                return ((Calendar) DATATYPE_CONVERTER_PARSE_DATE_TIME_METHOD.invoke(null, dateTimeString)).getTimeInMillis();
            } catch (IllegalAccessException e) {
                throw new IllegalStateException(e);
            } catch (InvocationTargetException e) {
                throw (RuntimeException) e.getCause();
            }
        }

        @Override
        public String format(final long dateTime) {
            Calendar calendar = Calendar.getInstance();
            calendar.setTimeInMillis(dateTime);
            calendar.setTimeZone(TimeZone.getTimeZone("Z"));
            try {
                return (String) DATATYPE_CONVERTER_PRINT_DATE_TIME_METHOD.invoke(null, calendar);
            } catch (IllegalAccessException e) {
                throw new IllegalStateException();
            } catch (InvocationTargetException e) {
                throw (RuntimeException) e.getCause();
            }
        }
    }

    static class Java8DateTimeFormatter implements FormatterImpl {



        static {
            try {
                Class.forName("java.time.format.DateTimeFormatter");
            } catch (ClassNotFoundException e) {
                throw new ExceptionInInitializerError(e);
            }
        }

        @Override
        public long parse(final String dateTimeString) {
            try {
                return ISO_OFFSET_DATE_TIME.parse(dateTimeString, new TemporalQuery<Instant>() {
                    @Override
                    public Instant queryFrom(final TemporalAccessor temporal) {
                        return Instant.from(temporal);
                    }
                }).toEpochMilli();
            } catch (DateTimeParseException e) {
                throw new IllegalArgumentException(e.getMessage());
            }
        }

        @Override
        public String format(final long dateTime) {
            return ZonedDateTime.ofInstant(Instant.ofEpochMilli(dateTime), ZoneId.of("Z")).format(ISO_OFFSET_DATE_TIME);
        }
    }

    private DateTimeFormatter() {
    }
}

package com.datastax.oss.driver.internal.core.type.util;

import java.io.DataInput;
import java.io.DataOutput;
import java.io.IOException;


public class VIntCoding {

  private static long readUnsignedVInt(DataInput input) throws IOException {
    int firstByte = input.readByte();


    if (firstByte >= 0) {
      return firstByte;
    }

    int size = numberOfExtraBytesToRead(firstByte);
    long retval = firstByte & firstByteValueMask(size);
    for (int ii = 0; ii < size; ii++) {
      byte b = input.readByte();
      retval <<= 8;
      retval |= b & 0xff;
    }

    return retval;
  }

  public static long readVInt(DataInput input) throws IOException {
    return decodeZigZag64(readUnsignedVInt(input));
  }



  private static int firstByteValueMask(int extraBytesToRead) {

    return 0xff >> extraBytesToRead;
  }

  private static byte encodeExtraBytesToRead(int extraBytesToRead) {

    return (byte) ~firstByteValueMask(extraBytesToRead);
  }

  private static int numberOfExtraBytesToRead(int firstByte) {




    return Integer.numberOfLeadingZeros(~firstByte) - 24;
  }

  private static final ThreadLocal<byte[]> encodingBuffer =
      ThreadLocal.withInitial(() -> new byte[9]);

  private static void writeUnsignedVInt(long value, DataOutput output) throws IOException {
    int size = VIntCoding.computeUnsignedVIntSize(value);
    if (size == 1) {
      output.write((int) value);
      return;
    }

    output.write(VIntCoding.encodeVInt(value, size), 0, size);
  }

  private static byte[] encodeVInt(long value, int size) {
    byte encodingSpace[] = encodingBuffer.get();
    int extraBytes = size - 1;

    for (int i = extraBytes; i >= 0; --i) {
      encodingSpace[i] = (byte) value;
      value >>= 8;
    }
    encodingSpace[0] |= encodeExtraBytesToRead(extraBytes);
    return encodingSpace;
  }

  public static void writeVInt(long value, DataOutput output) throws IOException {
    writeUnsignedVInt(encodeZigZag64(value), output);
  }


  private static long decodeZigZag64(final long n) {
    return (n >>> 1) ^ -(n & 1);
  }


  private static long encodeZigZag64(final long n) {

    return (n << 1) ^ (n >> 63);
  }


  public static int computeVIntSize(final long param) {
    return computeUnsignedVIntSize(encodeZigZag64(param));
  }


  private static int computeUnsignedVIntSize(final long value) {
    int magnitude =
        Long.numberOfLeadingZeros(
            value | 1); 
    return (639 - magnitude * 9) >> 6;
  }
}

package alien4cloud.utils;

import java.awt.Graphics2D;
import java.awt.Image;
import java.awt.RenderingHints;
import java.awt.image.BufferedImage;


public final class ImageResizeUtil {
    private ImageResizeUtil() {
    }


    public static BufferedImage resizeImage(final BufferedImage originalImage, final int width, final int height,
            final boolean preserveDimensions) {
        return resizeImage(originalImage, width, height, preserveDimensions, false);
    }


    public static BufferedImage resizeImageWithHint(BufferedImage originalImage, final int width, final int height,
            final boolean preserveDimensions) {
        return resizeImage(originalImage, width, height, preserveDimensions, true);
    }

    private static BufferedImage resizeImage(BufferedImage originalImage, final int width, final int height,
            final boolean preserveDimensions, final boolean enableHighQuality) {
        int type = originalImage.getType() == 0 ? BufferedImage.TYPE_INT_ARGB : originalImage.getType();

        int targetWidth = width;
        int targetHeight = height;

        if (preserveDimensions) {
            int[] targetDimentions = computeDimensions(width, height, originalImage.getWidth(), originalImage.getHeight());
            targetWidth = targetDimentions[0];
            targetHeight = targetDimentions[1];
        }

        BufferedImage resizedImage = new BufferedImage(targetWidth, targetHeight, type);

        Graphics2D g = resizedImage.createGraphics();
        if (enableHighQuality) {
            g.setRenderingHint(RenderingHints.KEY_INTERPOLATION, RenderingHints.VALUE_INTERPOLATION_BILINEAR);
            g.setRenderingHint(RenderingHints.KEY_RENDERING, RenderingHints.VALUE_RENDER_QUALITY);
            g.setRenderingHint(RenderingHints.KEY_ANTIALIASING, RenderingHints.VALUE_ANTIALIAS_ON);
        }

        g.drawImage(originalImage.getScaledInstance(targetWidth, targetHeight, Image.SCALE_SMOOTH), 0, 0, null);
        g.dispose();

        return resizedImage;
    }


    public static int[] computeDimensions(final int width, final int height, final int originalWidth, final int originalHeight) {
        int targetWidth = width;
        int targetHeight = height;

        float targetDimensions = (float) width / (float) height;
        float sourceDimensions = (float) originalWidth / (float) originalHeight;
        if (targetDimensions > sourceDimensions) {
            targetWidth = Float.valueOf(width * sourceDimensions / targetDimensions).intValue();
        } else {
            targetHeight = Float.valueOf(height * targetDimensions / sourceDimensions).intValue();
        }

        return new int[] { targetWidth, targetHeight };
    }
}

package org.xtreemfs.sandbox;

import java.io.File;
import java.io.RandomAccessFile;


public class writeTruncTest {


    public static void main(String[] args) {

        try {
            final String path = (args.length > 0) ? args[0] : "/tmp";
            final int    numObjs = (args.length > 1) ? Integer.valueOf(args[1]) : 8*1024;
            File f = new File(path+"/xtreemfs_testfile");
            f.delete();
            RandomAccessFile rf = new RandomAccessFile(f, "rw");
            long tStart = System.currentTimeMillis();
            long cnt = 0;
            long fsize = 0;
            for (int i = 0; i < 64*1024; i++) {
                if (cnt % 4096 == 0) {
                    fsize += 4096;
                    rf.setLength(fsize);
                }
                rf.writeLong(8l);
                rf.writeLong(64564516l);
                cnt += 16;

            }
            long tEnd = System.currentTimeMillis();
            System.out.println("duration (w/ trunc): "+(tEnd-tStart)+" ms");
            rf.close();

            f = new File(path+"/xtreemfs_testfile");
            f.delete();
            rf = new RandomAccessFile(f, "rw");
            tStart = System.currentTimeMillis();
            for (int i = 0; i < 64*1024; i++) {
                rf.writeLong(8l);
                rf.writeLong(64564516l);
            }
            tEnd = System.currentTimeMillis();
            System.out.println("duration (no trunc): "+(tEnd-tStart)+" ms");
            rf.close();
        } catch (Exception ex) {
            ex.printStackTrace();
        }
    }

}
